 
public class CharacterPrinter {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		double health = 12.5;
		int level;
		String name;
		name = "Mallis";
		level = 1;
		System.out.println("Level: " + level);
		health = 9;
		System.out.println("Health "+ health);
		System.out.println("Character Name: "+ name);
	}

}
